module Day06 where

import Data.List (tails, nub, findIndex)

solve :: IO ()
solve = do
  raw <- readFile "06/input.txt"
  print $ findMarker 4 raw
  print $ findMarker 14 raw


windows :: Int -> [a] -> [[a]]
windows n xs = take (length xs - n + 1) (windows' n xs)
  where windows' m ys = map (take m) $ tails ys

{-
>>> windows 4 "bvwbjplbgvbhsrlpgdmjqwftvncz"
["bvwb","vwbj","wbjp","bjpl","jplb","plbg","lbgv","bgvb","gvbh","vbhs","bhsr","hsrl","srlp","rlpg","lpgd","pgdm","gdmj","dmjq","mjqw","jqwf","qwft","wftv","ftvn","tvnc","vncz"]
-}

allDifferent :: Eq a => [a] -> Bool
allDifferent xs = length (nub xs) == length xs

{-
>>> allDifferent <$> windows 4 "bvwbjplbgvbhsrlpgdmjqwftvncz"
[False,True,True,True,True,True,True,False,True,True,True,True,True,True,True,True,True,True,True,True,True,True,True,True,True]
-}

firstAllDifferent :: [Bool] -> Maybe Int
firstAllDifferent = findIndex id

{-
>>> firstAllDifferent $ allDifferent <$> windows 4 ""
Just 1574
-}

findMarker :: Eq a => Int -> [a] -> Int
findMarker n xs = case firstAllDifferent $ allDifferent <$> windows n xs of 
  Just i  -> i + n
  Nothing -> -1

{-
>>> findMarker 4 ""
1578
-}
